import React from 'react';
import AddTaskForm from './AddTaskForm';
import Task from './Task'

const TaskContainer = input => {

  const props = input.props;

  const renderTasks = () => {
    const tasks = this.getTasks(props.tasks, props.selectedCategory)

    return tasks.map(task => {
      const props = { text: task.text, category: task.category, deleteTask: this.props.deleteTask };
      return <Task key={task.text} props={props} />
    });
  }

  const getTasks = (tasks, category) => tasks.filter(task => task.category === category || category === 'All')

  return (
    <div className='tasks'>
      <h5>Tasks</h5>
      <AddTaskForm addTask={props.addTask} />
      {renderTasks()}
    </div>
  )
}

export default TaskContainer;
