import React from 'react';

const task = (props) => {
  const task = props.task;

  return (
    <div className='task'>
      <div className='label'>{task.category}</div>
      <div className='text'>{task.text}</div>
      <button
        className="delete"
        onClick={() => task.deleteTask({ text: task.text, category: task.category })}
      >
        X
          </button>
    </div>
  )
}

export default task;
